import base64 as b64
import mimetypes as mt

texto = ""

def descifrartexto(texto):
    texto_str = texto.encode('utf-8')
    texto_2 = b64.b64decode(texto_str)
    resultado = texto_2.decode('utf-8')
    return resultado
   
